import React, { useState, useCallback } from 'react';
import { Form, Select, Upload, Button, message, Space } from 'antd';
import { UploadOutlined, ReloadOutlined } from '@ant-design/icons';
import type { UploadFile, UploadProps } from 'antd/es/upload/interface';
import Input from 'antd/es/input/Input';

const { Option } = Select;

type QueryType = 'rangeQuery' | 'knnQuery' | 'spatialJoins' | null;
type SpatialDataType = 'polygon' | 'points' | 'line' | null;
type RangeQuerySetType = 'polygon' | 'box' | null;
type KNNQuerySetType = 'point' | 'polygon' | null;
type PredicateType = 'intersect' | 'inside' | 'contains' | 'covers' | 'coveredBy' | 'equals' | 'meet' | 'findRelation' | null;

const HomePage: React.FC = () => {
  const [form] = Form.useForm();
  const [queryType, setQueryType] = useState<QueryType>(null);
  const [spatialDataType, setSpatialDataType] = useState<SpatialDataType>(null);
  const [querySetType, setQuerySetType] = useState<RangeQuerySetType | KNNQuerySetType | null>(null);
  const [predicate, setPredicate] = useState<PredicateType>(null);

  const [datasetFile, setDatasetFile] = useState<UploadFile | null>(null);
  const [queryDatasetFile, setQueryDatasetFile] = useState<UploadFile | null>(null); // For Range and KNN
  const [leftDatasetFile, setLeftDatasetFile] = useState<UploadFile | null>(null); // For Spatial Joins
  const [rightDatasetFile, setRightDatasetFile] = useState<UploadFile | null>(null); // For Spatial Joins


  const handleQueryTypeChange = (value: QueryType) => {
    setQueryType(value);
    setSpatialDataType(null);
    setQuerySetType(null);   
    setPredicate(null);      
    setDatasetFile(null);
    setQueryDatasetFile(null);
    setLeftDatasetFile(null);
    setRightDatasetFile(null);
    // reset all relevant fields by their form item names
    form.resetFields([
      'spatialDataType',
      'querySetType',
      'predicate',
      'datasetFile',
      'queryDatasetFile',
      'leftDatasetFile',
      'rightDatasetFile',
      'kValue',
    ]);
  };

  const commonUploadProps: UploadProps = {
    accept: '.bin', // Only allow .bin files
    beforeUpload: (file) => {
      const isBin = file.name.endsWith('.bin');
      if (!isBin) {
        message.error(`${file.name} is not a .bin file!`);
      }
      return isBin || Upload.LIST_IGNORE; // Prevent upload if not .bin
    },
    maxCount: 1, // Only allow one file
    onRemove: (file) => {
      // Logic to clear the respective file state when removed
      if (file.uid === datasetFile?.uid) setDatasetFile(null);
      if (file.uid === queryDatasetFile?.uid) setQueryDatasetFile(null);
      if (file.uid === leftDatasetFile?.uid) setLeftDatasetFile(null);
      if (file.uid === rightDatasetFile?.uid) setRightDatasetFile(null);
      return true; // Allow removal
    },
  };

  const customRequest = useCallback((options: any, setter: React.Dispatch<React.SetStateAction<UploadFile | null>>) => {
    const { file, onSuccess } = options;
    setter(file);
    onSuccess('ok'); // Mark as successful even though we're not uploading
  }, []);

  const handleFinish = async (values: any) => {
    message.loading('Processing query...', 0);

    const formData = new FormData();
    formData.append('queryType', values.queryType || '');
    if (values.spatialDataType) formData.append('spatialDataType', values.spatialDataType);
    if (values.querySetType) formData.append('querySetType', values.querySetType);
    if (values.kValue) formData.append('kValue', values.kValue);
    if (values.predicate) formData.append('predicate', values.predicate);
    if (queryType === 'rangeQuery' || queryType === 'knnQuery') {
      if (datasetFile && datasetFile.originFileObj) {
        formData.append('datasetFile', datasetFile.originFileObj);
      }
      if (queryDatasetFile && queryDatasetFile.originFileObj) {
        formData.append('queryDatasetFile', queryDatasetFile.originFileObj);
      }
    } else if (queryType === 'spatialJoins') {
      if (leftDatasetFile && leftDatasetFile.originFileObj) {
        formData.append('leftDatasetFile', leftDatasetFile.originFileObj);
      }
      if (rightDatasetFile && rightDatasetFile.originFileObj) {
        formData.append('rightDatasetFile', rightDatasetFile.originFileObj);
      }
    }

    try {
        const response = await fetch('http://localhost:5000/process-query', { // replace with your backend URL
            method: 'POST',
            body: formData,
        });

        message.destroy(); // remove loading message

        if (response.ok) {
            const result = await response.json();
            message.success('Query submitted successfully!');
            console.log('Backend response:', result);
            // You might want to display the results to the user
        } else {
            const errorText = await response.text();
            message.error(`Query failed: ${errorText}`);
            console.error('Backend error:', response.status, errorText);
        }
    } catch (error) {
        message.destroy();
        message.error('Network error or server unavailable.');
        console.error('Fetch error:', error);
    }
  };

  const handleResetForm = () => {
    form.resetFields();
    setQueryType(null);
    setSpatialDataType(null);
    setQuerySetType(null);
    setPredicate(null);
    setDatasetFile(null);
    setQueryDatasetFile(null);
    setLeftDatasetFile(null);
    setRightDatasetFile(null);
    message.info('Form cleared!');
  };

  return (
    <Form
      form={form}
      layout="vertical"
      onFinish={handleFinish}
      initialValues={{ queryType: null }}
      style={{ maxWidth: 600, margin: '20px auto', padding: '20px', border: '1px solid #eee', borderRadius: '8px' }}
    >
      <Form.Item
        name="queryType"
        label="Query Type"
        rules={[{ message: 'Please select a query type!'}]}
      >
        <Select placeholder="Select a query type" onChange={handleQueryTypeChange}>
          <Option value="rangeQuery">Range Query</Option>
          <Option value="knnQuery">KNN Query</Option>
          <Option value="spatialJoins">Spatial Joins</Option>
        </Select>
      </Form.Item>

      {queryType && (
        <>
          {/* --- Common File Uploads for Range & KNN --- */}
          {(queryType === 'rangeQuery' || queryType === 'knnQuery') && (
            <>
              <Form.Item label="Dataset File" style={{ width: '100%' }}>
                <Upload
                  {...commonUploadProps}
                  customRequest={(options) => customRequest(options, setDatasetFile)}
                  fileList={datasetFile ? [datasetFile] : []}
                >
                  <Button icon={<UploadOutlined />} style={{ width: '100%' }}>Select Dataset File (.bin)</Button>
                </Upload>
              </Form.Item>

              <Form.Item label="Query Dataset File" style={{ width: '100%' }}>
                <Upload
                  {...commonUploadProps}
                  customRequest={(options) => customRequest(options, setQueryDatasetFile)}
                  fileList={queryDatasetFile ? [queryDatasetFile] : []}
                >
                  <Button icon={<UploadOutlined />} style={{ width: '100%' }}>Select Query Dataset File (.bin)</Button>
                </Upload>
              </Form.Item>

              {/* --- Specific Fields for Range Query --- */}
              {queryType === 'rangeQuery' && (
                <>
                  <Form.Item
                    name="spatialDataType"
                    label="Spatial Data Type"
                    rules={[{ required: true, message: 'Please select a spatial data type!' }]}
                  >
                    <Select placeholder="Select spatial data type" onChange={(value: SpatialDataType) => setSpatialDataType(value)}>
                      <Option value="polygon">Polygon</Option>
                      <Option value="points">Points</Option>
                      <Option value="line">Line</Option>
                    </Select>
                  </Form.Item>

                  <Form.Item
                    name="querySetType"
                    label="Query Set Type"
                    rules={[{ required: true, message: 'Please select a query set type!' }]}
                  >
                    <Select placeholder="Select query set type" onChange={(value: RangeQuerySetType) => setQuerySetType(value)}>
                      <Option value="polygon">Polygon</Option>
                      <Option value="box">Box</Option>
                    </Select>
                  </Form.Item>
                </>
              )}

              {/* --- Specific Fields for KNN Query --- */}
              {queryType === 'knnQuery' && (
                <>
                  <Form.Item
                    name="spatialDataType"
                    label="Spatial Data Type"
                    rules={[{ required: true, message: 'Please select a spatial data type!' }]}
                  >
                    <Select placeholder="Select spatial data type" onChange={(value: SpatialDataType) => setSpatialDataType(value)}>
                      <Option value="polygon">Polygon</Option>
                      <Option value="points">Points</Option>
                      <Option value="line">Line</Option>
                    </Select>
                  </Form.Item>

                  <Form.Item
                    name="querySetType"
                    label="Query Set Type"
                    rules={[{ required: true, message: 'Please select a query set type!' }]}
                  >
                    <Select placeholder="Select query set type" onChange={(value: KNNQuerySetType) => setQuerySetType(value)}>
                      <Option value="point">Point</Option>
                      <Option value="polygon">Polygon</Option>
                    </Select>
                  </Form.Item>

                  <Form.Item name="kValue" label="K Value (for KNN)" rules={[{ required: true, message: 'Please input K value!' }]}>
                    <Input type="number" placeholder="Enter K value" />
                  </Form.Item>
                </>
              )}
            </>
          )}

          {/* --- Fields for Spatial Joins --- */}
          {queryType === 'spatialJoins' && (
            <>
              <Form.Item label="Left Dataset File" style={{ width: '100%' }}>
                <Upload
                  {...commonUploadProps}
                  customRequest={(options) => customRequest(options, setLeftDatasetFile)}
                  fileList={leftDatasetFile ? [leftDatasetFile] : []}
                >
                  <Button icon={<UploadOutlined />} style={{ width: '100%' }}>Select Left Dataset File (.bin)</Button>
                </Upload>
              </Form.Item>

              <Form.Item label="Right Dataset File" style={{ width: '100%' }}>
                <Upload
                  {...commonUploadProps}
                  customRequest={(options) => customRequest(options, setRightDatasetFile)}
                  fileList={rightDatasetFile ? [rightDatasetFile] : []}
                >
                  <Button icon={<UploadOutlined />} style={{ width: '100%' }}>Select Right Dataset File (.bin)</Button>
                </Upload>
              </Form.Item>

              <Form.Item
                name="predicate"
                label="Predicate"
                rules={[{ required: true, message: 'Please select a predicate!' }]}
              >
                <Select placeholder="Select predicate" onChange={(value: PredicateType) => setPredicate(value)}>
                  <Option value="intersect">Intersect</Option>
                  <Option value="inside">Inside</Option>
                  <Option value="contains">Contains</Option>
                  <Option value="covers">Covers</Option>
                  <Option value="coveredBy">Covered By</Option>
                  <Option value="equals">Equals</Option>
                  <Option value="meet">Meet</Option>
                  <Option value="findRelation">Find Relation</Option>
                </Select>
              </Form.Item>
            </>
          )}

          {/* --- Action Buttons --- */}
          <Form.Item style={{marginLeft:"55%"}}>
            <Space>
              <Button type="default" icon={<ReloadOutlined />} onClick={handleResetForm}>
                Clear Form
              </Button>
              <Button type="primary" htmlType="submit">
                Submit Query
              </Button>
            </Space>
          </Form.Item>
        </>
      )}
    </Form>
  );
};

export default HomePage;
