// src/components/AppHeader.tsx
import React from 'react';
import { Typography, Tooltip } from 'antd';
import { InfoCircleOutlined } from '@ant-design/icons';

const { Title } = Typography;

const AppHeader: React.FC = () => {
  // The information text for the tooltip
  const hecatoncheirInfo = (
    <div>
      <p>
        **Hecatoncheir** is a prototype distributed big spatial data management tool
        built with MPI that offers lightning-fast spatial query processing.
      </p>
      <p>
        Inspired by Hecatoncheires, the sons of Gaia (mother earth). With their
        hundreds of hands, they multitask to scale spatial data management up to
        infinity.
      </p>
    </div>
  );

  return (
    <div style={{
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center', // Center content horizontally
      padding: '20px',
      backgroundColor: '#f0f2f5', // Light background for the header
      borderBottom: '1px solid #e8e8e8', // Separator line
      marginBottom: '20px' // Space below the header
    }}>
      <Title level={2} style={{ margin: 0, marginRight: 10 }}>
        Hecatoncheir
      </Title>
      <Tooltip title={hecatoncheirInfo} placement="right">
        <InfoCircleOutlined style={{ fontSize: '18px', color: '#1890ff' }} />
      </Tooltip>
    </div>
  );
};

export default AppHeader;
