import React, { useEffect } from 'react';
import { Form, Button, message} from 'antd';
import TextArea from 'antd/es/input/TextArea';


interface PC {
  nameOrIp: string;
}

interface ClusterSetupProps {
  onClusterInitialized: () => void;
  pcNamesInput: string;
  setPcNamesInput: (value: string) => void;
  currentPcs: PC[];
}

const ClusterSetup: React.FC<ClusterSetupProps> = ({
  onClusterInitialized,
  pcNamesInput,
  setPcNamesInput,
  currentPcs,
}) => {
  const [form] = Form.useForm();

  const handleTextAreaChange = (e: React.ChangeEvent<HTMLTextAreaElement>) => {
    setPcNamesInput(e.target.value);
    const names = e.target.value
      .split('\n')
      .map((name) => name.trim())
      .filter((name) => name !== '');
    // Ensure the 'pcs' field in the form is updated whenever the TextArea changes
    form.setFieldsValue({ pcs: names.map((name) => ({ nameOrIp: name })) });
  };

  // IMPORTANT: handleInitHec now accepts 'values' directly from Form's onFinish
  const handleInitHec = async (values: any) => { // <-- Changed: accepts 'values'
    message.loading('Initializing HEC cluster...', 0);

    // No need for form.getFieldsValue() anymore, 'values' already contains them
    const pcsToSend = (values.pcs || []).map((pc: { nameOrIp: string }) => ({
      ...pc,
      // Only append :1 if nameOrIp is not empty
      nameOrIp: pc.nameOrIp ? `${pc.nameOrIp}:1` : '',
    }));

    console.log("PCs to send to backend:", pcsToSend); // Log to verify

    const clusterConfig = {
      numPcs: pcsToSend.length,
      pcs: pcsToSend,
    };

    try {
      const response = await fetch('http://localhost:5000/init-hec', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(clusterConfig),
      });

      message.destroy(); // Hide loading message

      if (response.ok) {
        const result = await response.json();
        message.success('HEC cluster initialized successfully!');
        console.log('Backend response (init-hec):', result);
        onClusterInitialized();
      } else {
        const errorText = await response.text();
        message.error(`HEC initialization failed: ${errorText}`);
        console.error('Backend error (init-hec):', response.status, errorText);
      }
    } catch (error) {
      message.destroy(); // Hide loading message on network error
      message.error('Network error or server unavailable during HEC initialization.');
      console.error('Fetch error (init-hec):', error);
    }
  };


  // This useEffect ensures the form's 'pcs' field is initialized/updated when pcNamesInput changes
  useEffect(() => {
    const names = pcNamesInput
      .split('\n')
      .map((name) => name.trim())
      .filter((name) => name !== '');
    form.setFieldsValue({ pcs: names.map((name) => ({ nameOrIp: name })) });
  }, [pcNamesInput, form]);


  return (
    <Form
      form={form}
      layout="vertical"
      onFinish={handleInitHec} // <-- Changed: Now the Form's onFinish handles submission
      initialValues={{ pcs: [] }} // Ensures 'pcs' array is initialized for the form
      style={{ padding: '20px', border: '1px solid #eee', borderRadius: '8px', marginTop:"1.5%" }}
    >
      {/*
        Ant Design Forms expect Form.Item components to have a 'name' prop
        for values to be automatically collected by onFinish.
        Since our PCs are dynamically generated, the `pcs` field is set
        via `form.setFieldsValue` based on the TextArea, which is correct.
      */}
      <Form.Item label="PC Names/IPs in the Cluster (one per line)" required>
        <TextArea
          placeholder="Enter the names or IPs of the PCs, one per line"
          style={{ width: '100%', minHeight: '120px' }}
          value={pcNamesInput}
          onChange={handleTextAreaChange}
        />
      </Form.Item>

      <Form.Item style={{ marginTop: 20 }}>
        <Button
          type="primary"
          htmlType="submit" // This button will now trigger the form's onFinish
          size="large"
          style={{ width: '100%' }}
          disabled={currentPcs.length === 0} // Still disables if no PCs are entered
        >
          Init Hecatoncheir
        </Button>
      </Form.Item>
    </Form>
  );
};

export default ClusterSetup;
