// src/components/PCListDisplay.tsx
import React from 'react';
import { Collapse, Typography, Space, Input } from 'antd';
import { MinusCircleOutlined, PlusOutlined } from '@ant-design/icons'; // Import icons if you want to customize

const { Panel } = Collapse;
const { Text } = Typography;

interface PC {
  nameOrIp: string;
}

interface PCListDisplayProps {
  pcs: PC[];
}

const PCListDisplay: React.FC<PCListDisplayProps> = ({ pcs }) => {
  if (pcs.length === 0) {
    return null; // Don't render anything if there are no PCs
  }

  return (
    <Collapse
      bordered={false}
      defaultActiveKey={['1']} // Automatically expand the panel on load
      expandIconPosition="end" // Place the expand icon at the end
      style={{
        backgroundColor: '#f0f2f5', // Light background to distinguish it
        borderRadius: '8px',
        marginBottom: '20px',
        marginTop:"6.5%"
      }}
    >
      <Panel
        header={<Text strong>Identified PCs ({pcs.length})</Text>}
        key="1"
        style={{ borderBottom: 'none' }}
      >
        <div style={{ maxHeight: '25%', overflowY: 'auto' }}>
          {pcs.map((pc, index) => (
            <Space key={index} style={{ display: 'flex', marginBottom: 8 }} align="baseline">
              <Text style={{ minWidth: '60px' }}>PC {index + 1}:</Text>
              <Input value={pc.nameOrIp} readOnly style={{ flexGrow: 1 }} disabled={true} />
            </Space>
          ))}
        </div>
      </Panel>
    </Collapse>
  );
};

export default PCListDisplay;
