// src/App.tsx
import React, { useState, useEffect } from 'react';
import ClusterSetup from './components/set-up-hecatoncheir';
import PCListDisplay from './components/pc-list'; // Import PCListDisplay
import { Layout, Menu, theme, Typography, Spin, Col, Row, MenuProps } from 'antd'; // Import necessary Ant Design components
import {
  DesktopOutlined,
  PieChartOutlined,
  UserOutlined,
  SettingOutlined,
} from '@ant-design/icons'; // Example icons
import './App.css'; // Assuming you have some basic styling

const { Header, Content, Footer, Sider } = Layout;
const { Title } = Typography;

function getItem(label: React.ReactNode, key: React.Key, icon?: React.ReactNode, children?: MenuItem[]): MenuItem {
  return {
    key,
    icon,
    children,
    label,
  } as MenuItem;
}

type MenuItem = Required<MenuProps>['items'][number];

// Example for what might appear after initialization
const ClusterDashboard: React.FC = () => (
  <div style={{ padding: 24, minHeight: 360, background: '#fff' }}>
    <Title level={3}>HEC Cluster Dashboard</Title>
    <p>Your HEC cluster is initialized and ready!</p>
    <p>You can now see monitoring information, run commands, etc.</p>
  </div>
);

const App: React.FC = () => {
  const [collapsed, setCollapsed] = useState(false);
  const [isClusterInitialized, setIsClusterInitialized] = useState(false);
  const [pcNamesInput, setPcNamesInput] = useState<string>(''); // Lifted state
  const [currentPcs, setCurrentPcs] = useState<{ nameOrIp: string }[]>([]); // Lifted state

  const {
    token: { colorBgContainer, borderRadiusLG },
  } = theme.useToken();

  // Effect to update currentPcs whenever pcNamesInput changes
  useEffect(() => {
    const names = pcNamesInput
      .split('\n')
      .map((name) => name.trim())
      .filter((name) => name !== '');
    setCurrentPcs(names.map((name) => ({ nameOrIp: name })));
  }, [pcNamesInput]);

  const handleClusterInitialized = () => {
    setIsClusterInitialized(true);
    // You might want to navigate to a dashboard view or similar here
  };

  const items: MenuItem[] = [
    getItem('Dashboard', '1', <PieChartOutlined />),
    getItem('Nodes', '2', <DesktopOutlined />),
    getItem('Users', 'sub1', <UserOutlined />, [
      getItem('Tom', '3'),
      getItem('Bill', '4'),
      getItem('Alex', '5'),
    ]),
    getItem('Settings', 'sub2', <SettingOutlined />, [
      getItem('Cluster Config', '6'),
      getItem('Logs', '7')
    ]),
  ];

  return (
    <Layout style={{ minHeight: '100vh' }}>
      <Layout>
        <Header style={{ padding: 0, background: colorBgContainer }} />
        <Content style={{ margin: '0 16px' }}>
          <div
            style={{
              padding: 24,
              minHeight: 360,
              background: colorBgContainer,
              borderRadius: borderRadiusLG,
              marginTop: 16,
            }}
          >
            <Row gutter={20}> {/* Use Ant Design Row for overall layout */}
              <Col span={8}> {/* Left column for PCListDisplay */}
                <PCListDisplay pcs={currentPcs} />
              </Col>
              <Col span={16}> {/* Right column for the main content */}
                {!isClusterInitialized ? (
                  <ClusterSetup
                    onClusterInitialized={handleClusterInitialized}
                    pcNamesInput={pcNamesInput} // Pass state and setState down
                    setPcNamesInput={setPcNamesInput}
                    currentPcs={currentPcs}
                  />
                ) : (
                  <ClusterDashboard /> // Show dashboard once initialized
                )}
              </Col>
            </Row>
          </div>
        </Content>
        <Footer style={{ textAlign: 'center' }}>
          HEC Cluster Â©{new Date().getFullYear()} Created by You
        </Footer>
      </Layout>
    </Layout>
  );
};

export default App;
