// src/App.tsx
import React, { useState, useEffect } from 'react';
import './App.css'; // Keep your global App styles
import AppHeader from './components/app-header';
import ClusterSetup from './components/set-up-hecatoncheir'; // Your cluster setup form
import HomePage from './components/home-page'; // Your component with the query forms (the one you provided earlier)
import PCListDisplay from './components/pc-list'; // The component to display the list of PCs

import { Layout, Row, Col, Typography, theme } from 'antd'; // Import Ant Design layout components

const { Content, Footer } = Layout; // We'll manage Header separately as AppHeader is custom
const { Title } = Typography;

function App() {
  const [isClusterInitialized, setIsClusterInitialized] = useState(false);
  const [pcNamesInput, setPcNamesInput] = useState<string>(''); // Lifted state for TextArea input
  const [currentPcs, setCurrentPcs] = useState<{ nameOrIp: string }[]>([]); // Derived list of PCs

  const {
    token: { colorBgContainer, borderRadiusLG },
  } = theme.useToken(); // Use Ant Design's theme token for background colors

  // Effect to update currentPcs whenever pcNamesInput changes (for PCListDisplay)
  useEffect(() => {
    const names = pcNamesInput
      .split('\n')
      .map((name) => name.trim())
      .filter((name) => name !== '');
    setCurrentPcs(names.map((name) => ({ nameOrIp: name })));
  }, [pcNamesInput]);

  const handleClusterInitialization = () => {
    setIsClusterInitialized(true);
  };

  const handleTerminateHec = () => {
    setIsClusterInitialized(false);
    setPcNamesInput(''); // Clear PC input when terminating
    setCurrentPcs([]);   // Clear PC list
    // You might also want to trigger a backend call to truly terminate HEC here
  };

  return (
    <Layout style={{ minHeight: '100vh' }}>
      <header>
        <AppHeader /> {/* Your custom AppHeader component */}
      </header>
      <Content style={{ margin: '0 16px' }}>
        <div
          style={{
            padding: 24,
            minHeight: 'calc(100vh - 114px)', // Adjust minHeight to fill content area (considering header/footer height)
            background: colorBgContainer,
            borderRadius: borderRadiusLG,
            marginTop: 16,
          }}
        >
          <Row gutter={20}>
            {/* Left column for PCListDisplay - ALWAYS PRESENT */}
            <Col span={8}>
              <PCListDisplay pcs={currentPcs} />
            </Col>
            {/* Right column for the main content - Conditionally rendered */}
            <Col span={16}>
              {isClusterInitialized ? (
                <>
                  <Title level={4} style={{ color: 'black', marginBottom: 0 }}>STATUS: <span style={{ color: 'green' }}>Active</span></Title>
                  <HomePage onTerminateHec={handleTerminateHec} /> {/* Your HomePage component (query form) */}
                  <p style={{ textAlign: 'center', color: 'green', marginTop: '10px' }}>hec is up and running...</p>
                </>
              ) : (
                <>
                  <ClusterSetup
                    onClusterInitialized={handleClusterInitialization}
                    pcNamesInput={pcNamesInput}
                    setPcNamesInput={setPcNamesInput}
                    currentPcs={currentPcs} // Pass currentPcs to ClusterSetup for validation/button disabling
                  />
                  <p style={{ textAlign: 'center', color: 'lightgrey', marginTop: '10px' }}>hec is down...</p>
                </>
              )}
            </Col>
          </Row>
        </div>
      </Content>
      <Footer style={{ textAlign: 'center' }}>
        HEC Cluster Â©{new Date().getFullYear()} Created by You
      </Footer>
    </Layout>
  );
}

export default App;
