// src/App.tsx
import React, { useState, useEffect } from 'react';
import ClusterSetup from './components/set-up-hecatoncheir';
import PCListDisplay from './components/pc-list';
import { Layout, theme, Typography, Col, Row } from 'antd'; // Removed Menu, Spin, MenuProps
import './App.css';

const { Header, Content, Footer } = Layout; // Removed Sider

const { Title } = Typography;

// Example for what might appear after initialization
const ClusterDashboard: React.FC = () => (
  <div style={{ padding: 24, minHeight: 360, background: '#fff' }}>
    <Title level={3}>HEC Cluster Dashboard</Title>
    <p>Your HEC cluster is initialized and ready!</p>
    <p>This is where your cluster monitoring, control, and other functionalities would be displayed.</p>
    <p>You can see the list of identified PCs on the left.</p>
  </div>
);

const App: React.FC = () => {
  // Removed collapsed state as Sider is removed
  const [isClusterInitialized, setIsClusterInitialized] = useState(false);
  const [pcNamesInput, setPcNamesInput] = useState<string>(''); // Lifted state
  const [currentPcs, setCurrentPcs] = useState<{ nameOrIp: string }[]>([]); // Lifted state

  const {
    token: { colorBgContainer, borderRadiusLG },
  } = theme.useToken();

  // Effect to update currentPcs whenever pcNamesInput changes
  useEffect(() => {
    const names = pcNamesInput
      .split('\n')
      .map((name) => name.trim())
      .filter((name) => name !== '');
    setCurrentPcs(names.map((name) => ({ nameOrIp: name })));
  }, [pcNamesInput]);

  const handleClusterInitialized = () => {
    setIsClusterInitialized(true);
  };

  return (
    <Layout style={{ minHeight: '100vh' }}>
      {/* Removed Sider */}
      <Layout>
        <Header style={{ padding: 0, background: colorBgContainer }} />
        <Content style={{ margin: '0 16px' }}>
          <div
            style={{
              padding: 24,
              minHeight: 'calc(100vh - 114px)', // Adjust minHeight to fill content area, considering header/footer
              background: colorBgContainer,
              borderRadius: borderRadiusLG,
              marginTop: 16,
            }}
          >
            <Row gutter={20}>
              {/* Left column for PCListDisplay - Always present */}
              <Col span={8}>
                <PCListDisplay pcs={currentPcs} />
              </Col>
              {/* Right column for the main content - Conditionally rendered */}
              <Col span={16}>
                {!isClusterInitialized ? (
                  <ClusterSetup
                    onClusterInitialized={handleClusterInitialized}
                    pcNamesInput={pcNamesInput}
                    setPcNamesInput={setPcNamesInput}
                    currentPcs={currentPcs}
                  />
                ) : (
                  // After initialization, ClusterDashboard is rendered here
                  <ClusterDashboard />
                )}
              </Col>
            </Row>
          </div>
        </Content>
        <Footer style={{ textAlign: 'center' }}>
          HEC Cluster Â©{new Date().getFullYear()} Created by You
        </Footer>
      </Layout>
    </Layout>
  );
};

export default App;
