// src/components/ClusterSetup.tsx
import React, { useState, useEffect } from 'react';
import { Form, Input, Button, Space, InputNumber, message } from 'antd';
import { CheckCircleOutlined, SettingOutlined } from '@ant-design/icons';

interface ClusterSetupProps {
  onClusterInitialized: () => void;
}

const ClusterSetup: React.FC<ClusterSetupProps> = ({ onClusterInitialized }) => {
  const [form] = Form.useForm();
  // State to hold the value typed in the "Number of PCs" input temporarily
  const [pendingNumPcs, setPendingNumPcs] = useState<number | null>(0);
  // State that actually controls how many PC name/IP fields are rendered
  const [actualNumPcs, setActualNumPcs] = useState<number>(0);

  /**
   * Effect hook to synchronize the form's 'pcs' field list
   * with the `actualNumPcs` state.
   * This ensures that when `actualNumPcs` changes (e.g., after "Set PCs" is clicked),
   * the form's internal data structure for 'pcs' is updated to match.
   * It pre-fills the `pcs` array with empty objects, or clears it if `actualNumPcs` is 0.
   */
  useEffect(() => {
    if (actualNumPcs >= 0) {
      // Create an array of empty objects to set the initial state of the pcs list.
      // This will ensure the Form.Item components for each PC have a place in the form's data.
      const initialPcs = Array(actualNumPcs).fill({ nameOrIp: '' });
      form.setFieldsValue({ pcs: initialPcs });
    } else {
      // If actualNumPcs is somehow invalid, ensure the list is cleared.
      form.setFieldsValue({ pcs: [] });
    }
  }, [actualNumPcs, form]); // Dependency array: re-run when actualNumPcs or form instance changes

  /**
   * Handler for the "Set PCs" button click or "Enter" key press on the InputNumber.
   * Validates the `pendingNumPcs` and updates `actualNumPcs`, triggering the UI update.
   */
  const handleSetPcs = () => {
    const value = pendingNumPcs;
    // Validate the input: must be a number and non-negative.
    if (value === null || value < 0 || isNaN(value)) {
      message.error('Please enter a valid non-negative number for PCs.');
      setPendingNumPcs(0); // Reset input value in state
      setActualNumPcs(0); // Clear rendered PCs
      return;
    }
    // Update actualNumPcs, which will then trigger the useEffect to manage form fields.
    setActualNumPcs(value);
  };

  /**
   * Handler for the form submission (Init HEC button).
   * Collects form data and sends it to the backend.
   */
  const handleInitHec = async (values: any) => {
    // Basic validation to ensure the number of rendered PC fields matches the 'set' number.
    if (values.pcs && values.pcs.length !== actualNumPcs) {
        message.error('The number of PC fields does not match the set number of PCs. Please click "Set PCs" again.');
        return;
    }

    message.loading('Initializing HEC cluster...', 0); // Show loading message

    const clusterConfig = {
      numPcs: actualNumPcs, // Use the confirmed number of PCs
      pcs: values.pcs || [], // Get the array of PC configurations from the form
    };

    try {
      const response = await fetch('http://localhost:5000/init-hec', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json', // Specify content type for JSON payload
        },
        body: JSON.stringify(clusterConfig), // Send the cluster configuration as JSON
      });

      message.destroy(); // Hide loading message

      if (response.ok) {
        const result = await response.json();
        message.success('HEC cluster initialized successfully!');
        console.log('Backend response (init-hec):', result);
        onClusterInitialized(); // Callback to inform the parent component that setup is complete
      } else {
        const errorText = await response.text();
        message.error(`HEC initialization failed: ${errorText}`);
        console.error('Backend error (init-hec):', response.status, errorText);
      }
    } catch (error) {
      message.destroy();
      message.error('Network error or server unavailable during HEC initialization.');
      console.error('Fetch error (init-hec):', error);
    }
  };

  return (
    <Form
      form={form}
      layout="vertical"
      onFinish={handleInitHec}
      initialValues={{ pcs: [] }} // Initialize the 'pcs' field in the form
      style={{ maxWidth: 600, margin: '20px auto', padding: '20px', border: '1px solid #eee', borderRadius: '8px' }}
    >
      {/* Custom CSS to hide the spinner arrows from Ant Design's InputNumber */}
      <style>{`
        /* Hide spinner arrows for InputNumber specifically within this component */
        .hide-number-arrows .ant-input-number-handler-wrap {
          display: none !important;
        }
        .hide-number-arrows.ant-input-number {
          padding-right: 11px !important; /* Adjust padding if needed after hiding arrows */
        }
      `}</style>

      {/* Input for the number of PCs with a "Set PCs" button */}
      <Form.Item label="Number of PCs in the Cluster" required>
        <Space>
          <InputNumber
            min={0} // Minimum value allowed is 0
            value={pendingNumPcs} // Controls the value displayed in the input
            onChange={setPendingNumPcs} // Updates the pendingNumPcs state
            placeholder="Enter number of PCs"
            style={{ width: '100%', flexGrow: 1 }}
            onPressEnter={handleSetPcs} // Trigger handleSetPcs when Enter is pressed
            className="hide-number-arrows" // Apply custom CSS class
          />
          <Button onClick={handleSetPcs} icon={<SettingOutlined />}>
            Set PCs
          </Button>
        </Space>
      </Form.Item>

      {actualNumPcs > 0 && (
        <div style={{
          maxHeight: actualNumPcs > 5 ? '50vh' : 'none',
          overflowY: actualNumPcs > 5 ? 'auto' : 'visible',
          paddingRight: actualNumPcs > 5 ? '15px' : '15px' // Add padding to avoid content being under scrollbar
        }}>
          {/* Map over a range to create the exact number of PC input fields */}
          {Array.from({ length: actualNumPcs }).map((_, index) => (
            <Space key={index} style={{ display: 'flex', marginBottom: 8 }} align="baseline">
              <Form.Item
                // Name for the form field: 'pcs' array, at 'index', with 'nameOrIp' property
                name={['pcs', index, 'nameOrIp']}
                rules={[{ required: true, message: 'Please enter PC name/IP!' }]}
                label={`PC ${index + 1} Name/IP`} // Dynamic label for each PC
                style={{ flexGrow: 1 }}
              >
                <Input placeholder={`Enter PC ${index + 1} Name or IP`} />
              </Form.Item>
            </Space>
          ))}
        </div>
      )}

      {/* Submit button for HEC Initialization */}
      <Form.Item style={{ marginTop: 20 }}>
        <Button type="primary" htmlType="submit" icon={<CheckCircleOutlined />} size="large" block>
          Init HEC
        </Button>
      </Form.Item>
    </Form>
  );
};

export default ClusterSetup;
